///$tab Dynamic Report
Mått:
LOAD
*
INLINE `
Mått
{implode: {$.FinalMeasures}, '{ linebreak }'}
` ;
{linebreak}

Dim:
LOAD
*
INLINE `
Dimension
{implode: {$.FinalDimension}, '{ linebreak }'}
` ;

{linebreak}

Datum:
LOAD
*
INLINE `
Period
{implode: {$.FinalDate}, '{linebreak}'}
` ;

{linebreak}
// Create variables for dynamic report
// Maximum number of columns to show
LET MaxShowColumns = 10;
{linebreak}
// Button control variables
LET vDynReportAggr = 'SUM';
LET vDynReportDivisor = 1;
LET vDynReportRound = 0.01;
{linebreak}
Set DimensionField = SubField(GetFieldSelections(Dimension,'|',$(MaxShowColumns)),'|',$1);
Set MeasureField= SubField(GetFieldSelections(Mått,'|',$(MaxShowColumns)),'|',$1);
Set DateField= SubField(GetFieldSelections(Period,'|',$(MaxShowColumns)),'|',$1);
{linebreak}
SET c_color_table = 'ARGB($1,140,140,200)';
